import matplotlib.pyplot as pltclass Barchart_Visual:    """    This class is responsible for creating barchart visualizations.    """    def __init__(self, fig_le, fig_wi, columns, nan_quote):        """        Initialize the class with the figure length, width, columns and rate         of NaN values        :param fig_le: float - Figure length        :param fig_wi: float - Figure width        :param columns: list - List of columns of the dataset        :param nan_quote: list - List of NaN rate of the dataset        """        self.fig_le = fig_le        self.fig_wi = fig_wi        self.columns = columns        self.nan_quote = nan_quote        def plot_barchart(self):        """        Plot the barchart of the NaN rate for all columns of the dataset.        """        # create a new axis        ax = plt.axes()        # plot the bar chart        plt.bar(self.columns, self.nan_quote)        plt.title("NaN-rate for all columns of the dataset")        plt.ylabel("NaN-rate")        plt.setp(ax.get_xticklabels(), rotation=90, ha='right')        plt.grid(alpha=0.5)        plt.show()        